import React, { useRef, useState } from "react";
import { useImage } from "../context/ImageContext";
import Cropper from "react-cropper";
import "cropperjs/dist/cropper.css";

const AdjustmentSliders = () => {
  const { settings, setSettings, setPreview, image, preview, setImage } =
    useImage(); // Use `preview` from the context
  const [cropper, setCropper] = useState(null);
  const cropperRef = useRef(null);

  useEffect(() => {
    let objectUrl;

    if (typeof image !== "string") {
      objectUrl = URL.createObjectURL(image);
    }

    return () => {
      if (objectUrl) {
        URL.revokeObjectURL(objectUrl);
      }
    };
  }, [image]);

  // Handle adjustment changes (brightness, contrast, etc.)
  const handleChange = async (e) => {
    const { name, value } = e.target;
    const newSettings = { ...settings, [name]: parseFloat(value) };
    setSettings(newSettings);

    try {
      const response = await fetch("http://localhost:5000/api/images/process", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          ...newSettings,
          image: preview || image, // Use the current preview image or the original image
        }),
      });
      if (!response.ok) {
        throw new Error("Error applying adjustments");
      }
      const data = await response.json();
      setPreview(data.previewUrl); // Update preview with the processed image
    } catch (err) {
      console.error("Error applying adjustments", err);
    }
  };

  // Handle cropping

  const handleCrop = async () => {
    if (!cropper) return;

    // Resize the cropped image to a smaller size (e.g., 500x500) to reduce payload
    const croppedCanvas = cropper.getCroppedCanvas({
      width: 500, // Adjust the width to a reasonable size
      height: 500, // Adjust the height
    });

    // Convert the resized canvas to a base64 image
    const croppedImage = croppedCanvas.toDataURL("image/jpeg", 0.7); // 0.7 for 70% quality to reduce size

    // Update the preview with the resized cropped image
    setPreview(croppedImage);

    // Send the cropped and resized image to the backend
    try {
      const response = await fetch("http://localhost:5000/api/images/process", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ image: croppedImage, ...settings }), // Include current settings
      });

      if (!response.ok) {
        throw new Error("Error applying crop");
      }

      const data = await response.json();
      setPreview(data.previewUrl); // Update preview with processed image
    } catch (err) {
      console.error("Error applying crop", err);
    }
  };
  return (
    <div>
      {image && (
        <div>
          kk
          <Cropper
            src={typeof image === "string" ? image : URL.createObjectURL(image)}
            style={{ height: 400, width: "40%" }}
            initialAspectRatio={1}
            aspectRatio={1}
            guides={false}
            cropBoxResizable={false}
            ref={cropperRef}
            onInitialized={(instance) => setCropper(instance)}
          />
          <button onClick={handleCrop}>Crop and Apply Adjustments</button>
        </div>
      )}

      {/* Adjustment sliders */}
      <label>
        Brightness
        <input
          type="range"
          name="brightness"
          min="0"
          max="2"
          step="0.1"
          value={settings.brightness}
          onChange={handleChange}
        />
      </label>
      <label>
        Contrast
        <input
          type="range"
          name="contrast"
          min="0"
          max="2"
          step="0.1"
          value={settings.contrast}
          onChange={handleChange}
        />
      </label>
      <label>
        Saturation
        <input
          type="range"
          name="saturation"
          min="0"
          max="2"
          step="0.1"
          value={settings.saturation}
          onChange={handleChange}
        />
      </label>
      <label>
        Rotation
        <input
          type="range"
          name="rotation"
          min="0"
          max="360"
          step="1"
          value={settings.rotation}
          onChange={handleChange}
        />
      </label>
    </div>
  );
};

export default AdjustmentSliders;
