import React, { useState } from "react";
import { useImage } from "../context/ImageContext";

const AdjustmentSliders = () => {
  const { settings, setSettings, setPreview } = useImage();

  const handleChange = async (e) => {
    const { name, value } = e.target;
    const newSettings = { ...settings, [name]: value };
    setSettings(newSettings);

    try {
      const response = await fetch("http://localhost:5000/api/images/process", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(newSettings),
      });
      if (!response.ok) {
        throw new Error("Error applying adjustments");
      }
      const data = await response.json();
      setPreview(data.previewUrl);
    } catch (err) {
      console.error("Error applying adjustments", err);
    }
  };

  return (
    <div>
      <label>
        Brightness
        <input
          type="range"
          name="brightness"
          min="0"
          max="2"
          step="0.1"
          value={settings.brightness}
          onChange={handleChange}
        />
      </label>
      <label>
        Contrast
        <input
          type="range"
          name="contrast"
          min="0"
          max="2"
          step="0.1"
          value={settings.contrast}
          onChange={handleChange}
        />
      </label>
      <label>
        Saturation
        <input
          type="range"
          name="saturation"
          min="0"
          max="2"
          step="0.1"
          value={settings.saturation}
          onChange={handleChange}
        />
      </label>
      <label>
        Rotation
        <input
          type="range"
          name="rotation"
          min="0"
          max="360"
          step="1"
          value={settings.rotation}
          onChange={handleChange}
        />
      </label>
    </div>
  );
};

export default AdjustmentSliders;
