import React, { useRef, useState, useEffect } from "react";
import { useImage } from "../context/ImageContext";
import Cropper from "react-cropper";
import "cropperjs/dist/cropper.css";

const AdjustmentSliders = () => {
  const { settings, setSettings, setPreview, image } = useImage();
  const [cropper, setCropper] = useState(null);
  const [imageUrl, setImageUrl] = useState(null);
  const cropperRef = useRef(null);

  useEffect(() => {
    if (typeof image === "string") {
      setImageUrl(image);
    } else if (image instanceof Blob) {
      const objectUrl = URL.createObjectURL(image);
      setImageUrl(objectUrl);

      // Cleanup object URL when component unmounts or image changes
      return () => {
        URL.revokeObjectURL(objectUrl);
      };
    }
  }, [image]);

  const handleChange = async (e) => {
    const { name, value } = e.target;
    const newSettings = { ...settings, [name]: value };
    setSettings(newSettings);

    try {
      const response = await fetch("http://localhost:5000/api/images/process", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ ...newSettings, image: imageUrl }), // Send image URL
      });
      if (!response.ok) {
        throw new Error("Error applying adjustments");
      }
      const data = await response.json();
      setPreview(data.previewUrl);
    } catch (err) {
      console.error("Error applying adjustments", err);
    }
  };

  const handleCrop = async () => {
    if (!cropper) return;

    const croppedImage = cropper.getCroppedCanvas().toDataURL();
    setPreview(croppedImage);

    try {
      const response = await fetch("http://localhost:5000/api/images/process", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ image: croppedImage, ...settings }),
      });
      if (!response.ok) {
        throw new Error("Error applying crop");
      }
      const data = await response.json();
      setPreview(data.previewUrl);
    } catch (err) {
      console.error("Error applying crop", err);
    }
  };

  return (
    <div>
      {imageUrl && (
        <div>
          <Cropper
            src={imageUrl}
            style={{ height: 400, width: "40%" }}
            initialAspectRatio={1}
            aspectRatio={1}
            guides={false}
            cropBoxResizable={false}
            ref={cropperRef}
            onInitialized={(instance) => setCropper(instance)}
          />
          <button onClick={handleCrop}>Crop and Apply Adjustments</button>
        </div>
      )}
      <label>
        Brightness
        <input
          type="range"
          name="brightness"
          min="0"
          max="2"
          step="0.1"
          value={settings.brightness}
          onChange={handleChange}
        />
      </label>
      <label>
        Contrast
        <input
          type="range"
          name="contrast"
          min="0"
          max="2"
          step="0.1"
          value={settings.contrast}
          onChange={handleChange}
        />
      </label>
      <label>
        Saturation
        <input
          type="range"
          name="saturation"
          min="0"
          max="2"
          step="0.1"
          value={settings.saturation}
          onChange={handleChange}
        />
      </label>
      <label>
        Rotation
        <input
          type="range"
          name="rotation"
          min="0"
          max="360"
          step="1"
          value={settings.rotation}
          onChange={handleChange}
        />
      </label>
    </div>
  );
};

export default AdjustmentSliders;
