// components/ImageAdjustments.js
import React from "react";
import { useImage } from "./ImageContext";

const ImageAdjustments = () => {
  const { image, settings, setSettings, setPreview } = useImage(); // Access context

  // Handle real-time adjustments and send data to backend
  const handleAdjust = async () => {
    const formData = new FormData();
    formData.append("image", image); // Send the original image
    formData.append("brightness", settings.brightness);
    formData.append("contrast", settings.contrast);
    formData.append("saturation", settings.saturation);
    formData.append("rotation", settings.rotation);

    const response = await fetch("http://localhost:5000/process", {
      method: "POST",
      body: formData,
    });

    const blob = await response.blob();
    const previewURL = URL.createObjectURL(blob); // Update preview with new settings

    setPreview(previewURL);
  };

  return (
    <div>
      <label>
        Brightness:
        <input
          type="range"
          min="0"
          max="2"
          step="0.1"
          value={settings.brightness}
          onChange={(e) =>
            setSettings({ ...settings, brightness: e.target.value })
          }
        />
      </label>

      <label>
        Contrast:
        <input
          type="range"
          min="0"
          max="2"
          step="0.1"
          value={settings.contrast}
          onChange={(e) =>
            setSettings({ ...settings, contrast: e.target.value })
          }
        />
      </label>

      <label>
        Saturation:
        <input
          type="range"
          min="0"
          max="2"
          step="0.1"
          value={settings.saturation}
          onChange={(e) =>
            setSettings({ ...settings, saturation: e.target.value })
          }
        />
      </label>

      <label>
        Rotation:
        <input
          type="range"
          min="0"
          max="360"
          step="1"
          value={settings.rotation}
          onChange={(e) =>
            setSettings({ ...settings, rotation: e.target.value })
          }
        />
      </label>

      <button onClick={handleAdjust}>Apply Adjustments</button>
    </div>
  );
};

export default ImageAdjustments;
