import React, { useRef, useState } from "react";
import { useImage } from "../context/ImageContext";
import Cropper from "react-cropper";
import "cropperjs/dist/cropper.css";

const ImageUpload = () => {
  const { setImage, setPreview } = useImage();
  const [file, setFile] = useState(null);
  const [cropper, setCropper] = useState(null);
  const cropperRef = useRef(null);

  const handleUpload = async (event) => {
    const uploadedFile = event.target.files[0];
    if (!uploadedFile) return;

    const formData = new FormData();
    formData.append("image", uploadedFile);

    setFile(URL.createObjectURL(uploadedFile));
    setImage(uploadedFile);

    try {
      const response = await fetch("http://localhost:5000/api/images/upload/", {
        method: "POST",
        body: formData,
      });

      if (!response.ok) {
        throw new Error(`Upload failed: ${response.statusText}`);
      }

      const data = await response.json();
      if (data.previewUrl) {
        setPreview(data.previewUrl);
      } else {
        throw new Error("Preview URL not found in response");
      }
    } catch (err) {
      console.error("Error uploading image", err.message);
    }
  };

  const handleCrop = async () => {
    if (!cropper) {
      console.error("Cropper instance is not initialized");
      return;
    }

    const croppedCanvas = cropper.getCroppedCanvas();
    if (!croppedCanvas) {
      console.error("Unable to get cropped canvas");
      return;
    }

    const croppedImage = croppedCanvas.toDataURL();
    setPreview(croppedImage);

    // Send the cropped image to the backend for processing
    try {
      const response = await fetch("http://localhost:5000/api/images/process", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ image: croppedImage }),
      });
      if (!response.ok) {
        throw new Error("Error applying crop");
      }
      const data = await response.json();
      setPreview(data.previewUrl);
    } catch (err) {
      console.error("Error applying crop", err);
    }
  };

  return (
    <div>
      <input
        type="file"
        accept="image/png, image/jpeg"
        onChange={handleUpload}
      />
    </div>
  );
};

export default ImageUpload;
